installation.script:
#!/bin/bash
# Minecraft Vanilla Installation Script
cd /mnt/server

# Update packages
apt update
apt install -y curl jq

# Create eula.txt
echo "eula=${EULA}" > eula.txt

# Download server jar based on version
if [ "${MINECRAFT_VERSION}" == "latest" ] || [ "${MINECRAFT_VERSION}" == "" ]; then
    echo "📥 Downloading latest Minecraft server..."
    DOWNLOAD_URL=$(curl -sSL https://launchermeta.mojang.com/mc/game/version_manifest.json | jq -r '.latest.release as $latest | .versions[] | select(.id == $latest) | .url')
    DOWNLOAD_URL=$(curl -sSL $DOWNLOAD_URL | jq -r '.downloads.server.url')
    VERSION=$(curl -sSL https://launchermeta.mojang.com/mc/game/version_manifest.json | jq -r '.latest.release')
else
    echo "📥 Downloading Minecraft ${MINECRAFT_VERSION}..."
    DOWNLOAD_URL=$(curl -sSL https://launchermeta.mojang.com/mc/game/version_manifest.json | jq -r --arg VERSION "$MINECRAFT_VERSION" '.versions[] | select(.id == $VERSION) | .url')
    DOWNLOAD_URL=$(curl -sSL $DOWNLOAD_URL | jq -r '.downloads.server.url')
    VERSION=${MINECRAFT_VERSION}
fi

if [ -z "$DOWNLOAD_URL" ]; then
    echo "❌ Error: Could not find download URL for version ${MINECRAFT_VERSION}"
    exit 1
fi

echo "🔗 Download URL: $DOWNLOAD_URL"
echo "📦 Minecraft Version: $VERSION"
curl -o ${SERVER_JARFILE} "$DOWNLOAD_URL"

if [ ! -f "${SERVER_JARFILE}" ]; then
    echo "❌ Error: Failed to download server jar"
    exit 1
fi

echo "⚙️ Creating basic server.properties..."
cat > server.properties << EOF
#Minecraft server properties
server-port=${SERVER_PORT}
motd=${MOTD}
max-players=${MAX_PLAYERS}
allow-flight=${ALLOW_FLIGHT}
allow-nether=${ALLOW_NETHER}
view-distance=${VIEW_DISTANCE}
seed=${SEED}
gamemode=${GAMEMODE}
force-gamemode=${FORCE_GAMEMODE}
hardcore=${HARDCORE}
white-list=${WHITELIST}
enable-rcon=true
rcon.port=${SERVER_PORT}
rcon.password=${RCON_PASSWORD}
online-mode=${ONLINE_MODE}
difficulty=${DIFFICULTY}
EOF

echo "✅ Installation completed successfully!"
echo "🎮 Minecraft $VERSION ready to start!"
echo "📊 Server will run on port ${SERVER_PORT}"
echo "👥 Max players: ${MAX_PLAYERS}"
